<?phpclass Users extends CI_Model{    public function __construct() {        parent::__construct();    }    public function record_count() {        return $this->db->count_all("users");    }    public function select() {        $this->db->select('*');        $this->db->from('users');        $this->db->order_by('user_id','DESC');        $query = $this->db->get();      if ($query->num_rows() > 0) {            foreach ($query->result() as $row) {                $data[] = $row;            }            return $data;        }        return false;    }    public function fetch_users($limit, $start) {        $this->db->select('*');        $this->db->from('users');        $this->db->order_by('user_id','DESC');        $this->db->limit($limit, $start);        $query = $this->db->get();      if ($query->num_rows() > 0) {            foreach ($query->result() as $row) {                $data[] = $row;            }            return $data;        }        return false;    }    public function insert($file){        $password=$this->input->post('user_pass',true);        $password=sha1(md5($password));        $data=array(            "user_name"=>$this->input->post('user_name'),            "user_username"=>$this->input->post('user_username'),            "user_pass"=>$password,            "user_email"=>$this->input->post('user_email'),            "role_id_fk"=>$this->input->post('role_id_fk'),            "user_phone"=>$this->input->post('user_phone'),            "type"=>$this->input->post('role_id_fk'),            //"vounter_id"=>$this->input->post('vounter_id'),            "user_photo"=>$file        );        if($this->input->post('vounter_id'))            $data['vounter_id'] = $this->input->post('vounter_id');        $done=$this->db->insert('users',$data);        if($done==1){            return true;        }else{            return false;        }    }    public function display($id){$this->db->where('user_id',$id);        $query=$this->db->get('users');        return $query->row_array();    }    public function update($id,$user_photo){     $data=array(    'user_name'=>$this->input->post('user_name'),    'user_username'=>$this->input->post('user_username'),    'user_email'=>$this->input->post('user_email'),    "role_id_fk"=>$this->input->post('role_id_fk'),    'user_phone'=>$this->input->post('user_phone'),    "type"=>$this->input->post('role_id_fk'),    "vounter_id"=>$this->input->post('vounter_id'),);    /*($this->input->post('role_id_fk'))        {$data['role_id_fk'] = $this->input->post('role_id_fk');}*/        if($user_photo !=''){            $data['user_photo']=$user_photo;        }        $this->db->where('user_id',$id);       $update= $this->db->update('users',$data);        if($update==1){           return true;        }       return false;    }    public function delete($id){$this->db->where('user_id',$id);        $this->db->delete('users');    }    public function check_user_data(){        $this->db->select('*');        $this->db->from('users');        $this->db->where('user_username',$this->input->post('user_name'));        $this->db->where('user_pass',sha1(md5($this->input->post('user_pass'))));        $query=$this->db->get();        if($query->num_rows()>0){            return $query->row_array();        }else{            return false;        }    } //-------------------------------------------- public function update_pass($id){      $password=$this->input->post('user_pass',true);        $password=sha1(md5($password));    $data["user_pass"]=$password;    $this->db->where('user_id',$id);    $this->db->update('users',$data); }       }//END CLASS 